import React from 'react';
import { StyleSheet, Text, View, Button } from 'react-native';
import Constants from 'expo-constants'
import PropTypes from 'prop-types'
import Counter from './Counter.js'


export default class App extends React.Component {
    constructor(props){
      super(props) 
          this.state = {
              switchCounter : true,    
          }
        this.handleWorkState = this.handleWorkState.bind(this)
        this.handleBreakState = this.handleBreakState.bind(this)
       }

    handleBreakState(){
        this.setState({switchCounter :false })
    }

    handleWorkState(){
        this.setState({switchCounter : true})
    }

    render() {
      if(this.state.switchCounter){  
        console.log(this.state.switchCounter)     
    return (

    <View style={styles.container}>
      <View style={styles.breakButton}>
        <Button title="Work timer"   />
        <Button title="Break timer" onPress={this.handleBreakState} />

        </View>


      <View style={styles.timer} >
      <Counter time = {10} />

      </View>

    </View>
  )
      } else {
          console.log(this.state.switchCounter) 
        return (

    <View style={styles.container}>
      <View style={styles.breakButton}>
        <Button title="Work timer" onPress={this.handleWorkState}  />
        <Button title="Break timer"  />

        </View>


      <View style={styles.timer} >
      <Counter time = {5} />

      </View>

    </View>
  )
      }
    }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',

  },
breakButton:{
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    paddingTop: Constants.statusBarHeight+5,


},   


timer:{
    flexDirection:'column',
    alignItems: 'center',
    justifycontent: 'center',
    paddingTop: 10,
},    
});
